import React from 'react'
import { useState } from 'react'
import "../styles/Inventory.css";
import Item from "./InventoryItem";
import RemoveCircleOutlineIcon from '@mui/icons-material/RemoveCircleOutline';
import Button from '@mui/joy/Button';
import Add from '@mui/icons-material/Add';
import FormControl from '@mui/joy/FormControl';
import FormLabel from '@mui/joy/FormLabel';


function Inventory(props) {

  const [listItem, setListItem] = useState([]);

  const handlerUpdateList = (list) =>{
    setListItem(list);
  };

  const importData = (path) => {
    /*
    const data =""
    const reader = new FileReader();
    reader.readAsText(path);
    reader.onload = () => {
      data=reader.result;
    }

    data = JSON.parse(data);
    console.log(data)
    */
   const data =  {terminal: {
                    ordinateur_portable: [1500,50,5],
                    ordinateur_fixe: [400,40,4],
                    serveur: [1000,100,10]
                  },
                  peripherique: {   
                    clavier: [100,10,1],
                    souris: [50,5,0.5],
                    ecran: [300,30,3],
                    imprimante: [300,30,3]
                  }
                }  
    return data
  }

  const data = importData("../../backend/BD.Json")

  const handlerDeleteItem = (id) => {
    alert("handlerDeleteItem")
    console.log(id)
    let tempList = [...listItem];
    console.log('tempList : ',tempList)
    tempList = tempList.filter(item => item.id !== id);
    setListItem(tempList);
    console.log('tempList : ',tempList)
  };

  const delButton = (id) => {return (<FormControl>
                                      <FormLabel>Delete</FormLabel>
                                      <Button startDecorator={<RemoveCircleOutlineIcon />} color="danger" high={20} width={20} onClick={() => handlerDeleteItem(id)} />
                                    </FormControl>);
                            }

  const handlerAddItem = () => {
    alert("handlerAddItem")
    const scrollingElement = (document.scrollingElement || document.body);
    scrollingElement.scrollTop = scrollingElement.scrollHeight;

    let tempList = [...listItem];
    let itemId = null;

    if (listItem.length===0){itemId=0;}
    else{itemId=(listItem[listItem.length-1].id)+1;};

    console.log('listItem : ',listItem)
    tempList.push({   id: itemId,
                      item :<Item list={listItem} setList={handlerUpdateList} id={itemId} data={data} cost={props.cost} setCost={props.setCost} delButton ={delButton(itemId)} />
                  })

    setListItem(tempList);
    console.log('list : ',listItem)
  };

  return (
    <div className='inventory'>
      {listItem.map((item) => {return <div className='item' key ={item.id} >{item.item} {item.id}</div>})}
      <Button startDecorator={<Add />} 
              color="neutral"
              variant="solid" 
              onClick={handlerAddItem}
              style={{
                borderRadius: 30,
                padding: "18px 36px",
                fontSize: "20px"
            }}
              size="lg">Add Item</Button>
    </div>
  )
}

export default Inventory
