import React from 'react'
import { useState, useEffect, createContext } from 'react'
import "../styles/Inventory.css";
import Item from "./InventoryItem";
import Button from '@mui/joy/Button';
import Add from '@mui/icons-material/Add';
import IconButton from '@mui/material/IconButton';
import DeleteIcon from '@mui/icons-material/Delete';


function Inventory(props) {

  const [listItem, setListItem] = useState([]);
  const [backendData, setBackendData] = useState([{}]);

  const ListItemContext = createContext();

  useEffect(() => {
    fetch("http://localhost:5000/getCostBd"
    ).then(
      response => response.json()
    ).then(
      data => {
        setBackendData(data);
      }
    )
  },[]);

  const handlerDeleteItem = (id) => {
    let tempList = [...listItem];
    tempList = tempList.filter(item => item.id !== id);
    setListItem(tempList);
  };

  const delButton = (id) => {return ( <IconButton color="tertiary" onClick={() => handlerDeleteItem(id)}>
                                          <DeleteIcon />
                                      </IconButton>);
                            }

  const handlerUpdateItemCost = (listUpdated) => {
    /*
    let tempList =  [...listItem];
    console.log ("templist",tempList);
    let find = false;
    const OOB = tempList.length;
    let compteur = 0;

    while(find === false & compteur < OOB){
      if (tempList[compteur].id ===id){
        tempList[compteur].cost = newCost;
        find = true;
      }
      compteur++;
    }

    if (find === true){
      setListItem(tempList);
      console.log ("templist",tempList);
    }else{
      alert("ERROR: Out of bounds on list when check for item ",id," in handlerUpdateItemCost")
    }
    //props.setTotalCost(newCost);
    //console.log('inventory new cost: ', newCost)
    */
    setListItem(listUpdated)
    console.log('listItem', listItem);
  };


  const handlerAddItem = () => {
    let tempList = [...listItem];
    let itemId = null

    const scrollingElement = (document.scrollingElement || document.body);
    scrollingElement.scrollTop = scrollingElement.scrollHeight;

    if (listItem.length===0){itemId=0;}
    else{itemId=(listItem[listItem.length-1].id)+1;};

    tempList.push({   id: itemId,
                      cost:[0,0,0],
                      item :<Item id={itemId} data={backendData} updateListCost={handlerUpdateItemCost}/>
                  })

    setListItem(tempList);
  };

  return (
    <div className='inventory'>
      <ListItemContext.Provider value={ listItem }>
        {listItem.map((item) => {return <div className='item' key ={item.id} >{item.item} {delButton(item.id)}</div>})}
      </ListItemContext.Provider>
      <Button startDecorator={<Add />} 
              color="neutral"
              variant="solid" 
              onClick={handlerAddItem}
              style={{
                borderRadius: 30,
                padding: "18px 36px",
                fontSize: "20px"
            }}
              size="lg">Add Item</Button>
      <Button startDecorator={<Add />} 
              color="neutral"
              variant="solid" 
              onClick={()=>{return}}
              style={{
                borderRadius: 30,
                padding: "18px 36px",
                fontSize: "20px"
            }}
              size="lg">Import IT inventory</Button>
    </div>
  )
}

export const ListItemContext = createContext();
export default Inventory
