import React from 'react'
import { useEffect, useState } from 'react'
import "../styles/InventoryItem.css";
import Input from '@mui/joy/Input';
import Divider from '@mui/joy/Divider';
import Button from '@mui/joy/Button';
import RemoveCircleOutlineIcon from '@mui/icons-material/RemoveCircleOutline';
import FormControl from '@mui/joy/FormControl';
import FormLabel from '@mui/joy/FormLabel';


function InventoryItem(props) {

  const [quantity,setQuantity] = useState(0)
  const [itemId,setItemId] = useState(null)
  const [cost,setCost] = useState([0,0,0])

  let attribute = {
                    id:props.id,
                    itemId:itemId,
                    quantity:quantity,
                    cost:cost,
                  }

  let list = props.list
  const inputRef = React.useRef(null);

  function fetchCalculationData(itemId){
      setItemId(itemId)
      setCost([2,5,1])
  }

  function handleDelete(){
    list.forEach(element => {
      console.log(element.attribute)
      if (element.attribute.id === this.attribute.id){
        let updateList = list.splice(list.indexOf(element),1);
        props.setList(updateList);
      };
    });
  }

  return (
    <div className='item'>
      <FormControl>
        <FormLabel>Props</FormLabel>
        <Input         
              type="number"
              defaultValue={0}
              slotProps={{
                input: {
                  ref: inputRef,
                  min: 0,
                  max: 2000,
                  step: 1,
                },
              }}
              variant="soft" />
        <Divider orientation="vertical" />
      </FormControl>
      <FormControl>
        <FormLabel>Quantity</FormLabel>
        <Input placeholder="Type in hereâ€¦" variant="soft" />
        <Divider orientation="vertical" />
      </FormControl>
      <Button startDecorator={<RemoveCircleOutlineIcon />} color="danger" high={20} width={20} onClick={() => handleDelete()} />
    </div>
  )
}

export default InventoryItem
