import React from 'react'
import { useEffect, useState } from 'react'
import "../styles/InventoryItem.css";
import Input from '@mui/joy/Input';
import Divider from '@mui/joy/Divider';
import Button from '@mui/joy/Button';
import RemoveCircleOutlineIcon from '@mui/icons-material/RemoveCircleOutline';
import FormControl from '@mui/joy/FormControl';
import FormLabel from '@mui/joy/FormLabel';
import List from '@mui/joy/List';
import ListItem from '@mui/joy/ListItem';
import MenuList from '@mui/joy/MenuList';
import MenuItem from '@mui/joy/MenuItem';
import Typography from '@mui/joy/Typography';


function InventoryItem(props) {

  const [quantity,setQuantity] = useState(0)
  const [itemId,setItemId] = useState(null)
  const [cost,setCost] = useState([0,0,0])

  let attribute = {
                    id:props.id,
                    itemId:itemId,
                    quantity:quantity,
                    cost:cost,
                  }

  let list = props.list

  function fetchCalculationData(itemId){
      setItemId(itemId)
      setCost([2,5,1])
  }

  function handleDelete(){
    list.forEach(element => {
      console.log(element.attribute)
      if (element.attribute.id === this.attribute.id){
        let updateList = list.splice(list.indexOf(element),1);
        props.setList(updateList);
      };
    });
  }

  return (
    <div className='item'>
      <FormControl>
        <FormLabel>Props</FormLabel>
        <MenuList
      component="div"
      variant="outlined"
      size="sm"
      sx={{
        boxShadow: 'sm',
        flexGrow: 0,
        minWidth: 200,
        maxHeight: 240,
        overflow: 'auto',
      }}
    >
      {Object.entries(props.data).forEach(([key, value])  => (
        <List key={key}>
          {console.log(key,value)}
          <ListItem sticky>
            <Typography
              id={`sticky-list-demo-${key}`}
              level="body-xs"
              textTransform="uppercase"
              fontWeight="lg"
            >
              {key}
            </Typography>
          </ListItem>
          {Object.entries(value).forEach(([key, value])  => (
            <MenuItem key={key}>{key}</MenuItem>
          ))}
        </List>
      ))}
    </MenuList>
        <Divider orientation="vertical" />
      </FormControl>
      <FormControl>
        <FormLabel>Quantity</FormLabel>
        <Input 
              type="number"
              defaultValue={0}
              slotProps={{
                input: {
                  min: 0,
                  max: 2000,
                  step: 1,
                },
              }}
              onChange={ event => {setQuantity(event.target.value);console.log(quantity)}}
              sx={{
                "--Input-radius": "20px"
              }}
              variant="soft" />
        <Divider orientation="vertical" />
      </FormControl>
      <Button startDecorator={<RemoveCircleOutlineIcon />} color="danger" high={20} width={20} onClick={() => handleDelete()} />
    </div>
  )
}

export default InventoryItem
