import React from 'react'
import { useEffect, useState } from 'react'
import "../styles/Inventory.css";
import Item from "./InventoryItem";
import Button from '@mui/joy/Button';
import Add from '@mui/icons-material/Add';



function Inventory() {

  const [addItem, setAddItem] = useState(false);
  const [listItem, setListItem] = useState([]);

  useEffect(() => {
    const scrollingElement = (document.scrollingElement || document.body);
    scrollingElement.scrollTop = scrollingElement.scrollHeight;
    let id = listItem.length===0?0:(listItem[listItem.length-1].attribute.id)+1
    listItem.push(<Item list={listItem} setList={setListItem} id={id}/>)
    setListItem(listItem)
  }, [addItem]);

  return (
    <div className='inventory'>
      {listItem.map((item) => {return <div className='item'>{item}</div>})}
      <Button startDecorator={<Add />} 
              variant="outlined" 
              onClick={()=>{setAddItem(!addItem)}}
              style={{
                borderRadius: 30,
                padding: "18px 36px",
                fontSize: "20px"
            }}
              size="lg">Add Item</Button>
    </div>
  )
}

export default Inventory
